[C#]

// Create an empty MPX file
MPXFile file = new MPXFile();

// Retrieves the project and set the finish date.
MPXHeader prj = file.Project;
prj.StartDate = new System.DateTime(2004, 3, 1);

// Shedule project from finish date
prj.ScheduleFrom = ScheduleFrom.FromFinish;


